import { createSlice } from '@reduxjs/toolkit';

const changeEpidemicDataSlice = createSlice({
    name: 'EpidemicData',
    initialState: {
        data: {},
        option: [true, false, false, false],
    },
    reducers: {
        changeEpidemicData: (state, action) => {
            state.data = action.payload.data;
        },
        changeOption: (state, action) => {
            const e = action.payload.order;
            state.option[e] = !state.option[e];
        },
    },
});

export const { changeEpidemicData, changeOption } = changeEpidemicDataSlice.actions;
export default changeEpidemicDataSlice.reducer;
