import { useState, useEffect } from 'react';
import MainFrame from '../../mainframe/MainFrame'
import { getSupplyQuantityAPI } from '../../../service/userService'
import LineChart from '../../chart/LineChart';
import Dropdown from '../../dropdown/Dropdown';
import province from './../../../constant/province'

function SuppliesDisplay() {

  // Lấy data pandemic từ localstorage
  const pandemicData = (localStorage.getItem('pandemicData'))
    .split('2018@4139,.abc/&xyz')
    .filter((item) => item !== '')
    .map(e=>{
      const [id, name] = e.split(':');
      return {
        pandemic_id: id,
        pandemic_name: name,
      }
    });
    localStorage.setItem('pandemicOption', 1);

  const dispatch = useDispatch();
  const [chartData, setChartData] = useState({
    labels: ['No name'],
    datasets: [
      {
        label: "No name",
        data: [0],
      }
    ]
  });

  const getEpidemicData = async (province_id, pandemic_id) => {
    const data = await getSupplyQuantityAPI(province_id, pandemic_id);
    console.log(data);
    // setChartData({
    //   labels: data.dateRange,
    //   datasets: [
    //     {
    //       label: data.infection.title,
    //       data: data.infection.list.map(e=>e.quantity_in_today),
    //       borderColor: 'blue',
    //       backgroundColor: 'blue'
    //     },
    //     {
    //       label: data.recovered.title,
    //       data: data.recovered.list.map(e=>e.quantity_in_today),
    //       borderColor: 'green',
    //       backgroundColor: 'green'
    //     },
    //     {
    //       label: data.death.title,
    //       data: data.death.list.map(e=>e.quantity_in_today),
    //       borderColor: 'red',
    //       backgroundColor: 'red'
    //     },
    //   ]
    // })
  }

  const changePandemic = (option)=>{
    getEpidemicData(
      localStorage.getItem('epidemicDisplay_selectedProvinceId'), 
      2, 
    );
  }

  const changeProvince = (option)=>{
    const province_id = province.indexOf(option) + 1;
    localStorage.setItem('epidemicDisplay_selectedProvinceId', province_id);
    getEpidemicData(
      province_id, 
      2, 
    );
  }

  useEffect(()=>{
  })

  return (
    <MainFrame>
      <h1>Tra cứu số liệu Vật tư y tế</h1>
      <div className="grid grid-cols-4 gap-4 mt-5">
        <div className="col-span-1 ">
          <Dropdown 
            data={province} func={changeProvince} 
            // selectedOption = {parseInt(localStorage.getItem('epidemicDisplay_selectedProvinceId'))}
          />
          <Dropdown data={pandemicData.map(e=>e.pandemic_name)} func={changePandemic} 
            // selectedOption={parseInt(localStorage.getItem('pandemicOption'))}
          />
          {/* <MyDatePicker func={changeDate}/> */}
        </div>
        <div className="col-span-3">
          <LineChart data={chartData} />
        </div>
      </div>
    </MainFrame>
  )
}

export default SuppliesDisplay
