export const PATH = {
    HOME: '/',
    LOGIN: '/login',
    LOG_OUT: '/logout',
    SYSTEM: '/system'
};

export const PATH_API = {
    BASE_URL: 'http://localhost:8080/',
    LOGIN_API: 'api/login',
};

export const role = {
    ADMIN: 0,
    EXPERT: 1,
    GUEST: 2,
};

export const loginCondition = {
    LOGIN_SUCCESS: 0,
    EMAIL_NOT_EXIST: 1,
    FAILED_PASSWORD: 2,
};

export const errorCode = {
    NO_ERROR: 0,
    LOGIN_FAILED: 1,
}

export const menuItems = {
    ACCOUNT_MANAGE: "Quản lý tài khoản",
    CREATE_ACCOUNT: "Tạo tài khoản mới",
    MODIFY_ACCOUNT: "Chỉnh sửa tài khoản",
    DELETE_ACCOUNT: "Xóa tài khoản",
}

export const sidebarMenu = {
    ADMIN: [{
        type: menuItems.ACCOUNT_MANAGE,
        subItems: [
            menuItems.CREATE_ACCOUNT,
            menuItems.MODIFY_ACCOUNT,
            menuItems.DELETE_ACCOUNT,
        ]
    }],
    EXPERT: [],
    GUEST: [],
}
