import React, { useRef, useState } from 'react';
import FadeIn from '../../../effect/FadeIn';
import { insertSupplyAbilityAPI } from '../../../../service/userService';
import { IMPORT_PROPERTIES } from '../../../../constant/constant';
import { EXAMPLE } from '../../../../constant/example';
// Excel
import * as XLSX from 'xlsx';
import { saveAs } from 'file-saver';

const ImportButton = (props) => {
  const fileInputRef = useRef(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');

  const handleImport = () => {
    fileInputRef.current.value = null;
    fileInputRef.current.click();
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];

    // Check the file extension
    const fileExtension = file.name.split('.').pop().toLowerCase();
    if (fileExtension !== 'xlsx') {
      setErrorMessage('Lỗi định dạng. Hãy chọn file có phần mở rộng là \'xlsx\'');
      return;
    }

    const fileReader = new FileReader();

    fileReader.onload = (e) => {
      const arrayBuffer = e.target.result;
      const data = new Uint8Array(arrayBuffer);
      const workbook = XLSX.read(data, { type: 'array' });

      if (workbook.SheetNames.length === 0) {
        setErrorMessage('Lỗi định dạng. Hãy chọn file có phần mở rộng là \'xlsx\'');
        setSelectedFile(null);
        return;
      }

      setSelectedFile(file);
      setErrorMessage('');
    };

    fileReader.readAsArrayBuffer(file);
  };

  const downloadFile = (data, filename) => {
    const currentTime = new Date().getTime();
    const fileName = `${filename}_${currentTime}.xlsx`;
    // Tạo workbook mới
    const workbook = XLSX.utils.book_new();

    // Convert data to worksheet format
    // console.log(data);
    const worksheet = XLSX.utils.json_to_sheet(data);
    // console.log(fileName, workbook, worksheet);
    // Append worksheet to workbook
    XLSX.utils.book_append_sheet(workbook, worksheet, 'Data');
    // Write workbook to Excel file
    const excelBuffer = XLSX.write(workbook, { bookType: 'xlsx', type: 'buffer' });
    // Create a Blob from the buffer
    const blob = new Blob([excelBuffer], { type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' });
    // Download the file
    saveAs(blob, fileName);
  };

  const handleReadData = () => {
    if (!selectedFile) {
      return;
    }

    const fileReader = new FileReader();
    
    fileReader.onload = async (e) => {
      const arrayBuffer = e.target.result;
      const data = new Uint8Array(arrayBuffer);
      const workbook = await XLSX.read(data, { type: 'array' });
      let objectData = [], properties = [];

      // Đọc dữ liệu từ sheet đầu tiên (sheet index = 0)
      const sheetName = workbook.SheetNames[0];
      const worksheet = workbook.Sheets[sheetName];

      // Sử dụng XLSX.utils.sheet_to_json để chuyển đổi sheet thành mảng JSON
      const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 });

      
      if(jsonData.length > 1){
        switch (props.orderButton) {
          case '1':{
            properties = jsonData.shift();
            jsonData.forEach(e=>{
              let element = {}
              properties.forEach((prop, index) => {
                element[prop] = e[index];
              })
              objectData.push({
                pandemic_id: element.pandemic_id,
                province_id: element.province_id,
                supply_type_id: element.supply_type_id,
                supply_quantity: element.supply_quantity,
                ability: element.ability,
              })
            })
            break;
          }
          case '2':{
            console.log(2);
            break;
          }
          case '3':{
            console.log(3);
            break;
          }
          case '4':{
            console.log(4);
            break;
          }
          case '5':{
            console.log(5);
            break;
          }
          case '6':{
            console.log(6);
            break;
          }
          case '7':{
            console.log(7);
            break;
          }
          case '8':{
            console.log(8);
            break;
          }
          case '9':{
            console.log(9);
            break;
          }
          case '10':{
            console.log(10);
            break;
          }
          case '11':{
            console.log(11);
            break;
          }
          case '12':{
            console.log(12);
            break;
          }
          default:
            break;
        }

        properties = jsonData.shift();
        jsonData.forEach(e=>{
          let element = {}
          properties.forEach((prop, index) => {
            element[prop] = e[index];
          })
          objectData.push({
            pandemic_id: element.pandemic_id,
            province_id: element.province_id,
            supply_type_id: element.supply_type_id,
            supply_quantity: element.supply_quantity,
            ability: element.ability,
          })
        })
        // insertSupplyAbilityAPI(objectData);
        // console.log(properties,objectData);
      } else {
        setErrorMessage('Lỗi định dạng. Hãy chọn file có phần mở rộng là \'xlsx\'');
        return;
      }
    };

    fileReader.readAsArrayBuffer(selectedFile);
  };

  const handleDelete = () => {
    setSelectedFile(null);
    setErrorMessage('');
  };

  return (
    <div className='col-span-1'>
      <FadeIn>
        <div className="grid grid-cols-4 mt-5">
          <div className="col-span-3 pl-4"><h5>{props.elementName}</h5></div>
          <div className="col-span-1"><hr /></div>
        </div>
        {!selectedFile && 
          <button className="border-solid border-2 border-gray-500 rounded-lg w-64 h-9 hover:bg-gray-300 ml-4" onClick={handleImport}>
            Thêm file
          </button>}
        <input
          type="file"
          ref={fileInputRef}
          onChange={handleFileChange}
          style={{ display: 'none' }}
        />
        {selectedFile && (
          <div className=''>
            <p>Selected file: {selectedFile.name}</p>
            <button className="btn btn-danger w-64 ml-4" onClick={handleDelete}>
              Xóa
            </button>
            <button className="btn btn-success w-64 ml-4 mt-2" onClick={handleReadData}>
              Cập nhật dữ liệu lên hệ thống
            </button>
          </div>
        )}
        <p className="text-blue-500 underline cursor-pointer pl-4" onClick={()=>{downloadFile(EXAMPLE[props.orderButton-1].data, EXAMPLE[props.orderButton-1].name)}}>Download file mẫu</p>
        {errorMessage && <p>{errorMessage}</p>}
      </FadeIn>
    </div>
  );
};

export default ImportButton;
