import React, { useEffect, useState } from 'react';

const Dropdown = (props) => {
  const options = props.data;
  const [selectedOption, setSelectedOption] = useState(options[0]);
  console.log('Dropdown', props, selectedOption);

  const handleOptionChange = (e) => {
    const value = e.target.value;
    setSelectedOption(value);
    props.func(value);
  };

  useEffect(()=>{
    if(props && props.selectOption != 0){
      setSelectedOption(options[props.selectOption])
    } else {
      setSelectedOption(options[0])
    }
  }, [props.selectOption])

  return (
    <div>
      <div className="col-span-1 mt-5">
  <h3>Thống kê:</h3>
  <div className="mt-3 level-count-container">
    <div className="level-count">Cấp độ 1: {superData.filter((e) => e.level === 1).length}</div>
    <div className="level-count">Cấp độ 2: {superData.filter((e) => e.level === 2).length}</div>
    <div className="level-count">Cấp độ 3: {superData.filter((e) => e.level === 3).length}</div>
  </div>
</div>
        <select 
        value={selectedOption} 
        onChange={handleOptionChange} 
        className="py-2 px-4 rounded-md focus:outline-none w-full mt-4 border-2 border-gray-600">
            {options.map((option, index) => (
            <option key={index} value={option}>
                {option}
            </option>
            ))}
        </select>
        {/* <p>Selected option: {selectedOption}</p> */}
    </div>
  );
};

export default Dropdown;
