import { SCREEN_PATH } from "../../constant/constant";
import { useNavigate } from 'react-router-dom';

const navigate = useNavigate();

const goToAccManage= () => {
    localStorage.setItem('menuItemOrder', 0);
    navigate(SCREEN_PATH.ADMIN.ACCOUNT_MANAGE);
} 
const goToEpidemicAnalyse= () => {
    localStorage.setItem('menuItemOrder', 0);
    navigate(SCREEN_PATH.EXPERT.EPIDEMIC_ANALYSE);
} 
const goToSupplyAnalyse= () => {
    localStorage.setItem('menuItemOrder', 1);
    navigate(SCREEN_PATH.EXPERT.SUPPLIES_ANALYSE);
} 
const goToDistributionAnalyse= () => {
    localStorage.setItem('menuItemOrder', 2);
    navigate(SCREEN_PATH.EXPERT.DISTRIBUTION_ANALYSE);
} 
const goToDbModify= () => {
    localStorage.setItem('menuItemOrder', 3);
    navigate(SCREEN_PATH.EXPERT.DB_MODYFY);
} 
const goToEpidemicDisplay= () => {
    localStorage.setItem('menuItemOrder', 0);
    navigate(SCREEN_PATH.GUEST.EPIDEMIC_DISPLAY);
} 
const goToSuppliesDisplay= () => {
    localStorage.setItem('menuItemOrder', 1);
    navigate(SCREEN_PATH.GUEST.SUPPLIES_DISPLAY);
} 
const goToDistributionDisplay= () => {
    localStorage.setItem('menuItemOrder', 2);
    navigate(SCREEN_PATH.GUEST.DISTRIBUTION_DISPLAY);
} 

const NavigationSidebar = [
    // ADMIN: 
    [goToAccManage,],
    // EXPERT: 
    [
        goToEpidemicAnalyse,
        goToSupplyAnalyse,
        goToDistributionAnalyse,
        goToDbModify,
    ],
    // GUEST: 
    [
        goToEpidemicDisplay,
        goToSuppliesDisplay,
        goToDistributionDisplay,
    ]
]

export default NavigationSidebar;
