import React, {useEffect, useState} from 'react';

const EpidemicTable = (props) => {
  const [tableData, setTableData] = useState([]);

  useEffect(()=>{
    // console.log('props: ', props);
    // console.log(Object.keys(props.data).length);
    // console.log(props.data);
    if(Object.keys(props.data).length){
      const data = props.data;
      const tableDataTable = data.dateRange.map(e=>{return {date: e}});
      tableDataTable.forEach( e=>{
        const eDate = new Date(e.date);
        const infection = data.infection.list.find( m=>{
          // const mDate = new Date(m.date);
          return e.date==m.date
        })
        const recovered = data.recovered.list.find( m=>{
          // const mDate = new Date(m.date);
          return e.date==m.date
        })
        const death = data.death.list.find( m=>{
          // const mDate = new Date(m.date);
          return e.date==m.date
        })
        e.infection = infection;
        e.recovered = recovered;
        e.death = death;
      })
      console.log(tableDataTable);
      setTableData(tableDataTable)
    }
  }, [props]);

  return (
    <table className="border">
      <thead className='bg-white border-b dark:bg-gray-900 dark:border-gray-700'>
        <tr>
          <th className="" rowSpan="2">Thời gian</th>
          <th className="" colSpan="2">Số ca nhiễm</th>
          <th className="" colSpan="2">Số ca khỏi bệnh</th>
          <th className="" colSpan="2">Số ca tử vong</th>
        </tr>
        <tr>
          <th className="">Mới</th>
          <th className="">Tổng</th>
          <th className="">Mới</th>
          <th className="">Tổng</th>
          <th className="">Mới</th>
          <th className="">Tổng</th>
        </tr>
      </thead>
      <tbody>
        {tableData.map((row, index) => (
          <tr key={index}>
            <td className="border border-gray-300 p-2">{row.date}</td>
            <td className="border border-gray-300 p-2">{row.infection.quantity_in_today}</td>
            <td className="border border-gray-300 p-2">{row.infection.total_quantity}</td>
            <td className="border border-gray-300 p-2">{row.recovered.quantity_in_today}</td>
            <td className="border border-gray-300 p-2">{row.recovered.total_quantity}</td>
            <td className="border border-gray-300 p-2">{row.death.quantity_in_today}</td>
            <td className="border border-gray-300 p-2">{row.death.total_quantity}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default EpidemicTable;
