import React, {useEffect, useState} from 'react';

const EpidemicTable = (props) => {
  const [tableData, setTableData] = useState([
    {
      date: '',
      infection: {
        new: -1,
        total: -1
      },
      recovered: {
        new: -1,
        total: -1
      },
      death: {
        new: -1,
        total: -1
      }
    }
  ]);
  // Dữ liệu mẫu
  const data = [
    { time: '2022-01-01', newCases: 100, totalCases: 500, recovered: 200, deaths: 10 },
    { time: '2022-01-02', newCases: 150, totalCases: 650, recovered: 250, deaths: 15 },
    // Các dòng dữ liệu khác...
  ];

  useEffect(()=>{
    // console.log('props: ', props);
    const data = props.data;
    const tableLength = data.infection.length;
    // props.dateRange.forEach(()=>{

    // })
  }, [props]);

  return (
    <table className="border-collapse border border-gray-300">
      <thead className='bg-white border-b dark:bg-gray-900 dark:border-gray-700'>
        <tr>
          <th className="border border-gray-300 p-2" rowSpan="2">Thời gian</th>
          <th className="border border-gray-300 p-2" colSpan="2">Số ca nhiễm</th>
          <th className="border border-gray-300 p-2" colSpan="2">Số ca khỏi bệnh</th>
          <th className="border border-gray-300 p-2" colSpan="2">Số ca tử vong</th>
        </tr>
        <tr>
          <th className="border border-gray-300 p-2">Mới</th>
          <th className="border border-gray-300 p-2">Tổng</th>
          <th className="border border-gray-300 p-2">Mới</th>
          <th className="border border-gray-300 p-2">Tổng</th>
          <th className="border border-gray-300 p-2">Mới</th>
          <th className="border border-gray-300 p-2">Tổng</th>
        </tr>
      </thead>
      <tbody>
        {data.map((row, index) => (
          <tr key={index}>
            <td className="border border-gray-300 p-2">{row.time}</td>
            <td className="border border-gray-300 p-2">{row.newCases}</td>
            <td className="border border-gray-300 p-2">{row.totalCases}</td>
            <td className="border border-gray-300 p-2">{row.recovered}</td>
            <td className="border border-gray-300 p-2">{row.deaths}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default EpidemicTable;
