import { useSelector, useDispatch } from 'react-redux';
import { changeEpidemicData } from '../../../store/reducer/changeEpidemicDataSlice';
import { useEffect } from 'react';
import MainFrame from '../../mainframe/MainFrame'
import { getEpidemicDataAPI } from '../../../service/userService'
import LineChart from '../../chart/LineChart';

function EpidemicDisplay() {
  const EpidemicData = useSelector((state) => state.changeEpidemicData.data);
  const dispatch = useDispatch();
  const chartData = {
    labels: ['No name'],
    datasets: [
      {
        label: "No name",
        data: [0],
      }
    ]
  };

  const getEpidemicData = async (event)=>{
    const data = await getEpidemicDataAPI(2, 2, "2022-7-18");
    dispatch(changeEpidemicData(data));
    console.log(data);
    chartData = {
      labels: data.dateRange,
      datasets: {
          label: data.infection.title,
          data: data.infection.list.map(e=>{
            console.log(1);
            return e.quantity_in_today
          }),
        }
    }
    console.log(chartData);
  }

  // console.log(EpidemicData);
  const changeOption = (order) => {
    const optionLst = localStorage.getItem('optionEpidemicDisplay');
    optionLst[order] = !optionLst[order];
    localStorage.setItem('optionEpidemicDisplay', optionLst);
  }

  useEffect(()=>{

  })

  return (
    <MainFrame>
      <h1>EpidemicDisplay</h1>
      <div className="row mt-3 login btn btn-primary" onClick={getEpidemicData}>
        Láº¥y data
      </div>
      <LineChart/>
    </MainFrame>
  )
}

export default EpidemicDisplay
