import React, { useState } from 'react';

const Dropdown = () => {
  const options = ['Option 1', 'Option 2', 'Option 3', 'Option 4', 'Option 5', 'Option 6', 'Option 7', 'Option 8', 'Option 9', 'Option 10', 'Option 11', 'Option 12'];
  const [selectedOption, setSelectedOption] = useState(options[9]); // Phần tử thứ 10 là mặc định

  const handleOptionChange = (e) => {
    setSelectedOption(e.target.value);
  };

  return (
    <div>
        <select 
        value={selectedOption} 
        onChange={handleOptionChange} 
        className="py-2 px-4 rounded-md focus:outline-none w-full mt-4 border-2 border-gray-600">
            {options.map((option, index) => (
            <option key={index} value={option}>
                {option}
            </option>
            ))}
        </select>
        <p>Selected option: {selectedOption}</p>
    </div>
  );
};

export default Dropdown;
