import { createSlice } from '@reduxjs/toolkit';
import { role } from '../../constant/constant';

const changeRoleSlice = createSlice({
    name: 'changeRole',
    initialState: {
      role: role.GUEST,
      email: '',
    },
    reducers: {
      changeToGuest: state => {
        state.role = role.GUEST;
      },
      changeToAdmin: state => {
        state.role = role.ADMIN;
      },
      changeToExpert: state => {
        state.role = role.EXPERT;
      },
    },
  });

export const {changeToGuest, changeToAdmin, changeToExpert} = changeRoleSlice.actions;
export default changeRoleSlice.reducer;
