import React, { useRef, useState } from 'react';
import { read } from 'xlsx';

const ImportButton = () => {
  const fileInputRef = useRef(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');

  const handleImport = () => {
    fileInputRef.current.click();
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    const fileReader = new FileReader();

    fileReader.onload = (e) => {
      const arrayBuffer = e.target.result;
      const data = new Uint8Array(arrayBuffer);
      const workbook = read(data, { type: 'array' });

      if (workbook.SheetNames.length === 0) {
        setErrorMessage('Invalid file format. Please select an Excel file.');
        setSelectedFile(null);
        return;
      }

      setSelectedFile(file);
      setErrorMessage('');
    };

    fileReader.readAsArrayBuffer(file);
  };

  const handleDelete = () => {
    setSelectedFile(null);
    setErrorMessage('');
  };

  return (
    <div>
      <div className="grid grid-cols-8">
        <div className="col-span-1"><hr /></div>
        <div className="col-span-2 pl-4"><h4>Game dễ vl</h4></div>
        <div className="col-span-5"><hr /></div>
      </div>
      {!selectedFile && <button className="btn btn-primary w-64" onClick={handleImport}>
        Thêm file
      </button>}
      <input
        type="file"
        ref={fileInputRef}
        onChange={handleFileChange}
        style={{ display: 'none' }}
      />
      {selectedFile && (
        <div>
          <p>Selected file: {selectedFile.name}</p>
          <button className="btn btn-primary w-64" onClick={handleDelete}>
            Xóa
          </button>
        </div>
      )}
      {errorMessage && <p>{errorMessage}</p>}
    </div>
  );
};

export default ImportButton;
