import { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';

function AddAccForm() {
  const [showForm, setShowForm] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleAddAccount = () => {
    // Xử lý logic khi thêm tài khoản vào danh sách
    console.log('Thêm tài khoản:', username, password);
    setUsername('');
    setPassword('');
    setShowForm(false);
  };

  const handleCancel = () => {
    // Hủy bỏ thêm tài khoản, ẩn form
    setUsername('');
    setPassword('');
    setShowForm(false);
  };

  return (
    <div>
      {showForm && (
        <div>
          <h3>Thêm tài khoản</h3>
          <div className="mt-2">
            <input
                type="text"
                placeholder="Tài khoản"
                className="border border-gray-500 rounded-md py-2 px-4 focus:outline-none focus:ring-2 focus:ring-gray-500 h-10 w-56"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="mt-2">
            <input
                type="password"
                placeholder="Mật khẩu"
                className="border border-gray-500 rounded-md py-2 px-4 focus:outline-none focus:ring-2 focus:ring-gray-500 h-10 w-56"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <button className='btn btn-primary mt-4 mr-3 w-40' onClick={handleAddAccount}>Xác nhận</button>
          <button className='btn btn-danger mt-4 w-40' onClick={handleCancel}>Hủy bỏ</button>
        </div>
      )}

      {/* Icon dấu + */}
      {!showForm && 
        <FontAwesomeIcon 
            icon={faPlus}
            style={{ padding: '5px', width: '30px', height: '30px'}}
            onClick={() => setShowForm(true)}
            className='btn btn-primary'
            />
      }
      
    </div>
  );
}

export default AddAccForm;
