import { createSlice } from '@reduxjs/toolkit';

const changeEpidemicDataSlice = createSlice({
    name: 'EpidemicData',
    initialState: {
        data: {
            infection: [{
                date: '',
                quantity_in_today: 0,
                total_quantity: 0,
            }],
            recovered: [{
                date: '',
                quantity_in_today: 0,
                total_quantity: 0,
            }],
            death: [{
                date: '',
                quantity_in_today: 0,
                total_quantity: 0,
            }],
            level: [{
                date: '',
                quantity_in_today: 0,
                total_quantity: 0,
            }],
        },
    },
    reducers: {
        changeEpidemicData: (state, action) => {
            state.data = action.payload;
        },
    },
});

export const { changeEpidemicData } = changeEpidemicDataSlice.actions;
export default changeEpidemicDataSlice.reducer;
