import { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';

function AddAccForm() {
  const [showForm, setShowForm] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleAddAccount = () => {
    // Xử lý logic khi thêm tài khoản vào danh sách
    console.log('Thêm tài khoản:', username, password);
    setUsername('');
    setPassword('');
    setShowForm(false);
  };

  const handleCancel = () => {
    // Hủy bỏ thêm tài khoản, ẩn form
    setUsername('');
    setPassword('');
    setShowForm(false);
  };

  return (
    <div>
      {showForm && (
        <div>
          <h2>Thêm tài khoản</h2>
          <input
            type="text"
            placeholder="Tài khoản"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Mật khẩu"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={handleAddAccount}>Xác nhận</button>
          <button onClick={handleCancel}>Hủy bỏ</button>
        </div>
      )}

      {/* Icon dấu + */}
      <FontAwesomeIcon 
      icon={faPlus}
      size="lg" // Kích thước 25px*25px
      color="white" // Màu trắng cho dấu cộng
      style={{ background: 'blue', padding: '5px', borderRadius: '50%' }} // Nền xanh cho icon
      onClick={() => setShowForm(true)} />
    </div>
  );
}

export default AddAccForm;
